var flow_functions = {
    "clean_date": function(input){
        if (input == undefined) return "";
        //node.warn("Converting:"+input);
        var data=input.split("T");
        var time=data[1];
        data=data[0].split("-");

        var year = data[0].slice(0, 4);
        var month = data[0].slice(4,6);
        var day = data[0].slice(6, 8);

        var hours=time.slice(0,2);
        var mins = time.slice(2, 4);
        var secs = time.slice(4, 6);
        //var d=new Date(year,month,day,hours,mins,secs);

        var dd=year+"-"+month+"-"+day+"T"+hours+":"+mins+":"+secs+"Z"
        var d = new Date(dd);
        //node.warn("Converted:"+d);
        return d;
    }
};

flow.set("flow_functions", flow_functions);

